module.exports.ensureSSL = function (req, res, next)
{
    var options = options || {};
        
    function redirect()
    {
        console.log('redirect to SSL for ' + req.host + req.url);

        if (req.protocol === 'http')
            res.redirect('https://' + req.host + req.url);
        else 
            next();
    }

    redirect();
}
